refsection.programlisting:
// creation of the MASCARET model
[err, id] = createMASCARET();
assert_checktrue(id>0);

// get all the descriptions
[err,TabVar,TabDesc]=getDescVarMASCARET(id);
assert_checkequal(err,0);

// get one description
[err,varType,category,modify,dimVar]=getTypeVarMASCARET(id,TabVar(2));
assert_checkequal(err,0);   
disp(varType);
disp(category);
disp(modify);
disp(dimVar);

// model deletion
err = deleteMASCARET(id);
assert_checkequal(err,0);
